var expect = require('chai').expect,
	sinon = require('mocha-sinon'),
	edj = require('../lib/edj.js');


describe('edj', function() {
	
	beforeEach(function() {
		this.sinon.stub(console, 'log');
	});

	it('Should console.log `edj`', function() {
		edj();
		expect( console.log.calledOnce ).to.be.true;
		expect( console.log.calledWith('edj') ).to.be.true;
	});

});